import React from 'react';
import IconLink from "./IconLink";
import { Box } from "@mui/material";

function PortfolioBlock(props) {
   const { image, live, sourcefe, sourcebe, title } = props;
   return (
      <Box display={'flex'} flexDirection={'column'} justifyContent={'center'} alignItems={'center'}>
         {/* âœ… Set Image Size */}
         <Box 
            component={'img'} 
            src={image} 
            alt={'mockup'}
            sx={{
               width: '80%',       // Adjust width dynamically
               maxWidth: '80vh',  // Ensures images are not too large
               height: 'auto',     // Keeps aspect ratio
               borderRadius: '8px' // Optional: Adds rounded corners
            }}
         />
         <h1 style={{ fontSize: '2rem' }}>{title}</h1>

         <Box className={'portfolio'} display={'flex'} flexDirection={'column'} gap={'0.5rem'}
              alignItems={'center'} fontSize={'1.5rem'} py={'2rem'}>

            {live && live !== "Coming Soon" && (
               <Box p={1} border={'2px solid black'} borderRadius={'25px'}>
                  <IconLink link={live} title={'Live Site'} icon={'fa fa-safari'} />
               </Box>
            )}

            {sourcefe && (
               <Box p={1} border={'2px solid black'} borderRadius={'25px'}>
                  <IconLink link={sourcefe} title={'Source Code FE'} icon={'fa fa-code'} />
               </Box>
            )}

            {sourcebe && (
               <Box p={1} border={'2px solid black'} borderRadius={'25px'}>
                  <IconLink link={sourcebe} title={'Source Code BE'} icon={'fa fa-code'} />
               </Box>
            )}
         </Box>
      </Box>
   );
}

export default PortfolioBlock;
