import React, { useState } from 'react';
import IconLink from "./IconLink";
import { Box, Popover, Typography } from "@mui/material";
import { FaUser } from "react-icons/fa"; // Import the person icon

function PortfolioBlock(props) {
   const { image, live, sourcefe, sourcebe, title, contributors } = props;
   const [anchorEl, setAnchorEl] = useState(null);

   const handleClick = (event) => {
      setAnchorEl(event.currentTarget);
   };

   const handleClose = () => {
      setAnchorEl(null);
   };

   const open = Boolean(anchorEl);
   const id = open ? 'contributors-popover' : undefined;

   return (
      <Box display={'flex'} flexDirection={'column'} justifyContent={'center'} alignItems={'center'}>
         <Box component={'img'} src={image} alt={'mockup'} sx={{ width: '80%', maxWidth: '80vh', height: 'auto', borderRadius: '8px' }} />

         {/* Title with person icon */}
         <Box display="flex" alignItems="center" gap={1}>
            <h1 style={{ fontSize: '2rem' }}>{title}</h1>
            <FaUser style={{ cursor: 'pointer' }} onClick={handleClick} />
         </Box>

         {/* Popover for Contributors */}
         <Popover
            id={id}
            open={open}
            anchorEl={anchorEl}
            onClose={handleClose}
            anchorOrigin={{
               vertical: 'bottom',
               horizontal: 'center',
            }}
            transformOrigin={{
               vertical: 'top',
               horizontal: 'center',
            }}
         >
            <Box p={2}>
               <Typography variant="h6">Contributors</Typography>
               {contributors && contributors.length > 0 ? (
                  contributors.map((contributor, index) => (
                     <Box key={index} my={1}>
                        <IconLink link={contributor.github} title={contributor.name} icon={'fa fa-github'} />
                     </Box>
                  ))
               ) : (
                  <Typography>No contributors listed.</Typography>
               )}
            </Box>
         </Popover>

         {/* Links for live site, frontend, and backend */}
         <Box className={'portfolio'} display={'flex'} flexDirection={'column'} gap={'0.5rem'}
              alignItems={'center'} fontSize={'1.5rem'} py={'2rem'}>

            {live && live !== "Coming Soon" && (
               <Box p={1} border={'2px solid black'} borderRadius={'25px'}>
                  <IconLink link={live} title={'Live Site'} icon={'fa fa-safari'} />
               </Box>
            )}

            {sourcefe && (
               <Box p={1} border={'2px solid black'} borderRadius={'25px'}>
                  <IconLink link={sourcefe} title={'Source Code FE'} icon={'fa fa-code'} />
               </Box>
            )}

            {sourcebe && (
               <Box p={1} border={'2px solid black'} borderRadius={'25px'}>
                  <IconLink link={sourcebe} title={'Source Code BE'} icon={'fa fa-code'} />
               </Box>
            )}
         </Box>
      </Box>
   );
}

export default PortfolioBlock;
